
/*! Turn.js - v3 - simplified version for GitHub Pages */
(function($) {
  'use strict';

  if ($.fn.turn) return;

  $.fn.turn = function(options) {
    var $this = $(this);
    $this.css({
      position: 'relative',
      perspective: '2000px'
    });

    $this.children().css({
      position: 'absolute',
      width: '100%',
      height: '100%',
      transformOrigin: 'left center',
      transition: 'transform 1s'
    });

    let current = 0;
    const pages = $this.children();

    function showPage(index) {
      pages.hide().eq(index).show();
    }

    showPage(current);

    $this.on('click', function () {
      current = (current + 1) % pages.length;
      showPage(current);
    });

    return $this;
  };
})(jQuery);
